import React, { useState, useEffect } from 'react';
import classes from './App.module.css';
import image1 from './1.jpg';
import image2 from './2.jpeg';
import image3 from './3.jpg';
import image4 from './4.jpg';
import image5 from './5.jpg';
import image6 from './6.jpg';
import icon from './menu.png';
import search from './search (1).png';
import logo from './logo.png';

function App() {
  const images = [image1, image3, image4, image5, image6];
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [isHeaderVisible, setIsHeaderVisible] = useState(true); // Track header visibility

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000);

    return () => clearInterval(interval);
  }, [images.length]);

  useEffect(() => {
    // Add event listener for scroll
    window.addEventListener('scroll', handleScroll);
    return () => {
      // Remove event listener when component unmounts
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleScroll = () => {
    // Check if scrolled beyond the first block
    if (window.scrollY > window.innerHeight) {
      setIsHeaderVisible(false);
    } else {
      setIsHeaderVisible(true);
    }
  };

  return (
    <div className={classes.container}>
      <div className={classes.header} style={{ transform: isHeaderVisible ? 'translateY(0)' : 'translateY(-100%)' }}>

          <img src={logo} alt="Logo" className={classes.logo} />

        <div className={classes.searchBar}>
          <input type="text" placeholder="Search..." className={classes.searchInput} />
          <button className={classes.menuButton}>
            <img src={icon} alt="Menu Icon" className={classes.menuIcon} />
          </button>
        </div>
      </div>
      <div className={classes.firstBlock}>
        <div className={classes.block}>
          {images.map((image, index) => (
            <img
              key={index}
              src={image}
              alt={`Image ${index + 1}`}
              className={`${classes.image} ${index === currentImageIndex ? classes.active : ''}`}
            />
          ))}
        </div>
      </div>
      <div className={classes.secondBlock}>
        <div className={classes.block}>
          <img src={image2} alt="Second Block Image" className={classes.image} />
        </div>
      </div>
    </div>
  );
}

export default App;
