import React, { useState, useEffect } from 'react';
import classes from './App.module.css'; // Import CSS module for styling
import image1 from './1.jpg';
import image3 from './3.jpg';
import image4 from './4.jpg';
import image5 from './5.jpg';
import image6 from './6.jpg';
import icon from './menu.png';
import search from './search (1).png';
import logo from './logo.png';

function App() {
  const images = [image1, image3, image4, image5, image6];
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000); // Change image every 3 seconds

    return () => clearInterval(interval); // Cleanup function to clear interval
  }, [images.length]);

  return (
    <div className={classes.container}>
      <div className={classes.firstBlock}>
        <div className={classes.header}>
          <div className={classes.searchBar}>
            <input type="text" placeholder="Search..." className={classes.searchInput} />
            <button className={classes.menuButton}>
              <img src={icon} alt="Menu Icon" className={classes.menuIcon} />
            </button>
            <img src={search} alt="Search Icon" className={classes.searchIcon} />
          </div>
          <img src={logo} alt="Logo" className={classes.logo} />
        </div>
        <div className={classes.block}>
          {images.map((image, index) => (
            <img
              key={index}
              src={image}
              alt={`Image ${index + 1}`}
              className={`${classes.image} ${index === currentImageIndex ? classes.active : ''}`}
            />
          ))}
        </div>
      </div>

      <div className={classes.secondBlock}>
        <div className={classes.block}>
          <img src={image1} alt="Second Block Image" className={classes.image} />
        </div>
      </div>
    </div>
  );
}

export default App;
