import React, { useState, useEffect } from 'react';
import classes from './App.module.css';
import p1 from './1.jpg';
import p2 from './2.jpg';
import p3 from './3.jpg';
import p4 from './4.jpg';
import p5 from './5.jpg';

function App() {
  const images = [p1, p2, p3, p4, p5];
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000); // Change image every 3 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <div className={classes.container}>
      <div className={classes.heroSection}>
        <h1 className={classes.heading}>Welcome to AMNS</h1>
        <p className={classes.subheading}>Discover our amazing collection</p>
        <div className={classes.imageContainer}>
          {images.map((image, index) => (
            <img
              key={index}
              src={image}
              alt={`Image ${index + 1}`}
              className={`${classes.image} ${index === currentImageIndex ? classes.active : ''}`}
            />
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
